import React from "react";

//componente UserList usa el componente User que pinta el card con los datos dentro del <article>
const Character = (props) => {
  const getGender = () => {
    if (props.user.gender === "female") {
      return "Mujer";
    } else if (props.user.gender === "male") {
      return "Hombre";
    } else {
      return "No binario"; //porque no se han cogido los datos
    }
  };

  return (
    <article className="card">
      <img
        className="card__img"
        src={props.character.image}
        alt={`Foto de ${props.character.name}`}
        title={`Foto de ${props.character.name}`}
      />
      <h4 className="card__title">{props.character.name}</h4>
      <p className="card__description">{props.character.species}</p>
    </article>
  );
};

export default Character;
